import logo from './logo.svg';
import './App.css';
import React, { Component } from 'react';
import Chatbox from './Chatbox';

class App extends Component{
  constructor(props) {
    super(props);
    this.state = { 
      open: "false"
    };
  }

  handleOpenBox () {
    if(this.state.open === "tr"){
      this.setState({
        open: "false"
      });
    }
    else {
      this.setState({
        open: "true"
      })
    }
  }

  render (){
    return (
      <div className="App">
      <div class="bot" id="bot">
        {
          (this.state.open)? <Chatbox handleOpenBox={this.handleOpenBox}/> : <i onclick={this.handleOpenBox()} class="fas fa-robot"></i>
        }     
    </div>
      </div>
    );
  }
}

export default App;
