import logo from './logo.svg';
import './App.css';
import React, { Component } from 'react';
import Chatbox from './Chatbox';

class App extends Component{
  constructor(props) {
    super(props);
    this.state = { 
      open: false
    };
  }

  handleOpenBox () {
      this.setState({
        open: !this.state
      });

  }

  render (){
    return (
      <div className="App">
        <div className="bot" id="bot">
        {
          (this.state.open)? <Chatbox handleOpenBox={this.handleOpenBox}/> : <i onClick={this.handleOpenBox} className="fas fa-robot"></i>
        }     
        </div>
      </div>
    );
  }
}

export default App;
