import React, { Component } from 'react';

class Chatbox extends Component{

  render (){
    return(
      <div>
      <div className="chatbox">
        <div className="heading">
            <h1>iQmetrix<sup>Â®</sup></h1>
        </div>
        <div>
          <div className="message-box">
            {
              this.props.messages.count > 0 ? this.props.messages.map(msg => {
                return <p className= {`${msg.type}`}> {msg.response} </p>
              }) : null
            }
          </div>
          <form onSubmit={this.props.handleAddingItems}>
            <input className='message' 
                   type="text" placeholder="please enter a message" onChange={(e) => {this.props.handleOnchange(e)}} />
            <button>Submit</button>
          </form>
        </div>
      </div>
      <i  onClick={() => {this.props.handleOpenBox()}} className="fas fa-times"></i>
      </div>
    )
  }
}



export default Chatbox;
